  //     m=> m.id !== movie.id

  //   );
  //   this.setState(state => ({
  //     movies:newMovieList
  //   }))
  // }

  /// axios delete

  deleteMovie = async (movie) => {
    axios.delete(`http://localhost:3002/movies/${movie.id}`)
    const newMovieList = this.state.movies.filter(
      m => m.id !== movie.id

    );
    this.setState(state => ({
      movies: newMovieList
    }))
  }


  // filtrleme
  searchMovie = (event) => {
    this.setState({ searchQuery: event.target.value })
  }


  render() {
    //filtlrme fonskiyonu
    let filteredMovies = this.state.movies.filter(
      (movie) => {
        return movie.name.toLowerCase().indexOf(this.state.searchQuery.toLowerCase()) !== -1
      }
    )
    return (
      <BrowserRouter>

        <div className='container'>
      <switch>
          <Routes>
            <Route exact path="/" exact render={() => (
              <React.Fragment>
                <div className='row'>
                  <div className='col-12'>
                    <Serachbar
                      searchMovieProp={this.searchMovie}
                    />
                  </div>
                </div>
                <Movielist
                  movies={filteredMovies}
                  deleteITem={this.deleteMovie}
                />
              </React.Fragment>
            )}/>

            </Routes>

              <Routes>
                <Route exact  path="/Add" Component={<Addmovie/>}/>

              </Routes>
              </switch>
            </div >

            </BrowserRouter>

      // <BrowserRouter>

      //   <div className='container'>
      //     <div className='row'>
      //       <div className='col-12'>
      //         <Serachbar
      //           searchMovieProp={this.searchMovie}
      //         />
      //       </div>
      //     </div>
      //     <Movielist
      //       movies={filteredMovies}
      //       deleteITem={this.deleteMovie}
      //     />

      //     <Routes>
      //       <Route exact path="/add" Component={<Addmovie/>} />
      //     </Routes>

      //   </div >

      // </BrowserRouter >

    )
  }
}

export default App;
