import { Component } from 'react';

class Movielist extends Component{
    
    // yazı kısaltma fonskiyonu
    
     truncatingOver=()=>(string,maxLenght) => {
        if (!string) return null 
        if(!string.lenght<=maxLenght) return string;
        return `${string.substring(0,maxLenght)} ...`;
        
    }
    render(){
    
   return ( <div className='row'>
          
            {this.props.movies.map((movie,i) => (
                <div className='col-lg-4 ' key={movie.id}>
                    <div className='card mb-4 shadow-sm'>
                        <img className='card-img-top' alt="sample movie" src={movie.imageURL} />
                        <div className='card-body'>
                            <h5 className='card-title'>{movie.name}</h5>
                            <p className='card-text'>{truncatingOver(movie.overview,80)}</p>
                            <div className='d-flex justify-content-between align-items-center'>
                                <button className='btn btn-md btn-outline-danger' onClick={(event)=>this.props.deleteITem(movie)}>Delete</button>
                                <h2><span className='badge badege-info'>{movie.rating}</span></h2>
                            </div>
                        </div>
                    </div>
                </div>
            ))}

        </div>
   )
}
        
}

