  //   }))
  // }

  /// fetch yapısı
  // deleteMovie= async (movie)=>{
  //   const url=`http://localhost:3002/movies/${movie.id}`
  //   await fetch(url,{
  //     method:"DELETE"
  //   })
  //   const newMovieList=this.state.movies.filter(
  //     m=> m.id !== movie.id

  //   );
  //   this.setState(state => ({
  //     movies:newMovieList
  //   }))
  // }

  /// axios delete

  deleteMovie = async (movie) => {
    axios.delete(`http://localhost:3002/movies/${movie.id}`)
    const newMovieList = this.state.movies.filter(
      m => m.id !== movie.id

    );
    this.setState(state => ({
      movies: newMovieList
    }))
  }


  // filtrleme
  searchMovie = (event) => {
    this.setState({ searchQuery: event.target.value })
  }

  Addmovie = async (movie) => {
    await axios.post(`http://localhost:3002/movies/`, movie)

    this.setState(state => ({
      movies: state.movies.concat([movie])
    }))
  }

  //güncelleme
  onEditmovie = async (movie) => {
    await axios.put(`http://localhost:3002/movies/${id}`, updateMovie)

  }
  render() {
    //filtlrme fonskiyonu
    let filteredMovies = this.state.movies.filter(
      (movie) => {
        return movie.name.toLowerCase().indexOf(this.state.searchQuery.toLowerCase()) !== -1
      }
    ).sort((a, b) => {
      return a.id < b.id ? 1 : -1; // id seçimin terisne döndürür :D
    })
    return (
      <Router>
        <div className='container'>
          <Switch>
            <Route path="/edit/:id" render={(props) => (
              <Edit
                onEditmovie={(id,movie) => {
                  this.Edit(id,movie)

                }} />
            )} />







            {/* <Route path="/edit/:id" component={Edit} /> */}

            <Route path="/add" render={({ history }) => (
              <Addmovie
                onAddMovie={(movie) => {
                  this.Addmovie(movie)
                  history.push("/");
                }} />
            )} />

            <Route path="/" render={() => (
              <React.Fragment>
                <div className='row'>
                  <div className='col-12'>
                    <Serachbar
                      searchMovieProp={this.searchMovie}
                    />
                  </div>
                </div>
                <Movielist
                  movies={filteredMovies}
                  deleteITem={this.deleteMovie}
                />
              </React.Fragment>
            )} />

          </Switch>
        </div>
      </Router>
    )
  }
}
export default App;
